window.lucide = {
  createIcons: () => {
    document.querySelectorAll('[data-lucide]').forEach(el => {
        const iconName = el.getAttribute('data-lucide');
        const svg = `<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-${iconName}"><title>${iconName}</title></svg>`;
        const tempDiv = document.createElement('div');
        tempDiv.innerHTML = svg;
        const iconEl = tempDiv.firstChild;
        for(let i = 0; i < el.attributes.length; i++) {
            const attr = el.attributes[i];
            if(attr.name !== 'data-lucide') {
                iconEl.setAttribute(attr.name, attr.value);
            }
        }
        el.parentNode.replaceChild(iconEl, el);
    });
  }
};
